import styles from "../../app/styles/modules/plan.module.scss";

function Plan({
  title,
  price,
  subTitle,
  checkList,
  selected,
  icon,
}: {
  title: string;
  price: string;
  subTitle: string;
  checkList: string[];
  selected: boolean;
  icon: JSX.Element;
}) {
  return (
    <div className={`${styles.plan} ${selected && styles.selected}`}>
      <header>
        {icon}
        <h4 className="small-title">{title}</h4>
      </header>
      <div className="flex flex-col gap-4">
        <h2>
          {price}.<span>99</span>
        </h2>
        <span className="text-xs opacity-70">{subTitle}</span>
      </div>
      <ul>
        {checkList.map((check, id) => (
          <li key={id}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="14"
              height="14"
              viewBox="0 0 14 14"
              fill="none"
            >
              <mask
                id="mask0_351_11142"
                style={{ maskType: "alpha" }}
                maskUnits="userSpaceOnUse"
                x="0"
                y="0"
                width="14"
                height="14"
              >
                <rect width="14" height="14" fill="#D9D9D9" />
              </mask>
              <g mask="url(#mask0_351_11142)">
                <path
                  d="M5.83324 9.35925L3.70746 7.23347L4.31658 6.62435L5.83324 8.14102L9.68324 4.29102L10.2924 4.90013L5.83324 9.35925Z"
                  fill="black"
                />
              </g>
            </svg>
            <span className="text-xs">{check}</span>
          </li>
        ))}
      </ul>
      <button className="">
        <span className="flex-1 text-left text-xs opacity-70">
          {selected ? "Current Plan" : "Change Plan"}
        </span>
        <div className="vertical-sep"></div>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width="17"
          height="16"
          viewBox="0 0 17 16"
          fill="none"
        >
          <g opacity="0.7">
            <mask
              id="mask0_351_11164"
              style={{ maskType: "alpha" }}
              maskUnits="userSpaceOnUse"
              x="0"
              y="0"
              width="17"
              height="16"
            >
              <rect x="0.666626" width="16" height="16" fill="#D9D9D9" />
            </mask>
            <g mask="url(#mask0_351_11164)">
              <path
                d="M11.7512 8.49997H3.66663V7.5H11.7512L7.95381 3.70255L8.66661 3L13.6666 7.99998L8.66661 13L7.95381 12.2974L11.7512 8.49997Z"
                fill="black"
              />
            </g>
          </g>
        </svg>
      </button>
    </div>
  );
}

